// ProductCard.tsx
import React from "react";
import { FeaturedMarket } from "../../types/types"; // Importamos la interfaz FeaturedMarket
import "./ProductCard.css"; // Importamos los estilos CSS del componente

interface ProductCardProps {
  product: FeaturedMarket; // El producto es de tipo FeaturedMarket
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
  return (
    <div className="product-card">
      <img src={product.images[0].desktop} alt={product.title} />
      <div className="product-details">
        <h3 className="product-title">{product.title}</h3>
        <p className="product-destination">{product.destination}</p>
        <div className="product-info">
          <p className="product-days">{product.days} days</p>
          <p className="product-extra-nights">
            Extra nights: {product.extraNights}
          </p>
          <p className="product-price">
            Price: {product.priceDetail.fromPriceBeautify}
          </p>
        </div>
        <button className="view-details-btn">View Details</button>
      </div>
    </div>
  );
};

export default ProductCard;
