import React from "react";
import { useQuery } from "react-query";
import axios from "axios";

import { Botswana } from "../types/types";
import ProductCard from "../components/productCard/ProductCard";
import Search from "../components/search/Search";
import "./ProductsList.css";

const logo = require("../assets/exoticca-logo.png");

const ProductsList: React.FC = () => {
  const handleSearch = (query: string) => {
    // handle search logic here
  };

  const fetchBotswanaData = async () => {
    const { data } = await axios.get<Botswana>(
      "https://api-us.exoticca.com/api/landing/v2/country/botswana"
    );
    console.log("data Botswana", data);
    return data;
  };

  const {
    data: botswanaData,
    isLoading,
    isError,
  } = useQuery<Botswana, Error>("botswanaData", fetchBotswanaData);

  return (
    <div>
      <div className="header">
        <img src={logo} alt="Logo" className="logo" />
      </div>
      <div className="content">
        <div className="content__search">
          <Search onSearch={handleSearch} />
        </div>
        {isLoading ? (
          <div>Loading...</div>
        ) : isError ? (
          <div>Error fetching data</div>
        ) : (
          <>
            {botswanaData && (
              <div>
                <div className="content__title">
                  Our recommendations to visit {botswanaData.name} and
                  neighbouring countries
                </div>
                {botswanaData.destinations.featuredMultiMarket.map(
                  (featuredMultiMarketProduct) => (
                    <ProductCard product={featuredMultiMarketProduct} />
                  )
                )}
                <div className="content__title">
                  Multi country vacation packages including {botswanaData.name}
                </div>
                {botswanaData.destinations.multiMarket.map(
                  (multiMarketProduct) => (
                    <ProductCard product={multiMarketProduct} />
                  )
                )}
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
};

export default ProductsList;
