// ProductCard.tsx

import React from "react";
import { FeaturedMarket } from "../../types/types";
import "./ProductCard.css";

interface ProductCardProps {
  product: FeaturedMarket;
}

const ProductCard: React.FC<ProductCardProps> = ({ product }) => {
  const isMobile = window.innerWidth <= 768; // Consideramos cualquier ancho menor o igual a 768px como móvil

  return (
    <div className={`product-card ${isMobile ? "mobile" : "desktop"}`}>
      <img
        src={isMobile ? product.images[0].mobile : product.images[0].desktop}
        alt={product.title}
      />
      <div className="product-details">
        <div className="product-details-information">
          <h3 className="product-title">{product.title}</h3>
          <p className="product-destination">{product.destination}</p>
          <div className="product-info">
            <p className="product-days">{product.days} days</p>
            <p className="product-extra-nights">
              Extra nights: {product.extraNights}
            </p>
          </div>
          <div className="product-tags">
            {product.tags.map((tag) => (
              <span key={tag.id} className="tag">
                {tag.alias}
              </span>
            ))}
          </div>
        </div>
        <div className="product-details-price">
          <p className="product-discount">
            {product.priceDetail.pricingPercentage}%
          </p>
          <p className="product-old-price">
            From {product.priceDetail.oldPriceBeautify}
          </p>
          <p className="product-price">
            {product.priceDetail.fromPriceBeautify}
          </p>
          <p className="product-price-per-night">
            Per night: {product.priceDetail.pricePerNight}
          </p>
          <button className="view-details-btn">See Trip</button>
        </div>
      </div>
    </div>
  );
};

export default ProductCard;
